(2, 24, 2, 'Валентина', 'genodessa@gmail.com', '2025-05-15 10:11:59', 'Гарна якість. Рекомендую', 10, 1, '172.31.0.1'),
(3, 25, 2, 'Максим', 'genodessa@gmail.com', '2025-02-18 11:30:24', 'Смачний чай', 10, 1, '172.31.0.1'),
(4, 17, 0, 'Марина', 'genodessa@gmail.com', '2025-02-06 14:16:47', 'лимони сушені все добре', 10, 1, ''),
(5, 24, 0, 'Анна ', 'genodessa@gmail.com', '2025-01-25 14:17:22', 'Смачний справжній узвар!', 10, 1, ''),
(6, 24, 0, 'Бертолом Иллеш', 'genodessa@gmail.com', '2024-12-26 14:18:11', 'Все супер.\r\nДуже добрий набір, якщо залити самогонкою і дати постояти 2 тиждня, смак вийде чудовий.\r\nПереваги\r\nСмак.\r\nНедоліки\r\nНедоліків не ма.', 10, 1, ''),
(7, -1, 0, 'Марина', '', '2025-07-29 11:37:46', 'Все сподобалось.Буду замовляти.', 10, 1, '172.31.0.1'),
(8, -1, 0, 'Влада', '', '2025-07-16 11:37:46', 'Швидко відправили, зв\'язалися, красиво запакували і поклали в подарунок м\'яту.\r\nДякую, дуже смачно', 10, 1, '172.31.0.1'),
(9, -1, 0, 'Наталья', '', '2025-07-12 11:37:46', 'Дуже приємна співпраця.Дякую.', 10, 1, '172.31.0.1'),
(10, 24, 0, 'Анна', 'qq@q.ua', '2024-12-28 11:46:53', 'Саме той набір на узвар, який я шукала! З цілою грушею, з чорносливом! Запах і смак неймовірні!', 10, 1, ''),
(11, -1, 0, 'Олеся ', '', '2024-12-20 11:37:46', 'Дякую за подарунок', 10, 1, '172.31.0.1'),
(12, -1, 0, 'Наталия', '', '2024-11-23 11:37:46', 'Дуже задоволена товаром та обслуговуванням. Товар був доставлений вчасно і найкращої якості. Дуже задоволена , що товар був завакумований , це збереже його якість надовго. Цього немає у інших продавців цього товару. Рекомендую цю фірму всім.', 10, 1, '172.31.0.1'),
(13, -1, 0, 'Андрій ', '', '2024-08-24 11:37:46', 'Гарне обслуговування', 10, 1, '172.31.0.1');
